import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent implements OnInit {
  signupForm!: FormGroup;
  constructor() {}
  ngOnInit(): void {
    this.signupForm = new FormGroup({
      email: new FormControl('', [Validators.required, Validators.email]),
      password: new FormControl('', [
        Validators.required,
        Validators.pattern(
          /(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@$!%*#?&^_-]).{8,}/
        ),
      ]),
    });
  }
  onSubmit() {
    console.log(this.signupForm);
  }
}
<div class="login-container">
    <div class="login-content">
        <form [formGroup]="signupForm" (ngSubmit)="onSubmit()">
            <div class="form-group1">
                <label for="email" >Email</label>
                <input type="email" name="" id="email" formControlName="email" class="form-control">
                <span *ngIf="!signupForm.get('email').valid && signupForm.get('email').touched">Please enter a valid email!</span>
            </div>
            <div class="form-group2">
                <label for="password">Password</label>
                <input type="text" name="" id="password" formControlName="password"  class="form-control">
                <span *ngIf="!signupForm.get('password').valid && signupForm.get('password').touched">Please enter a valid password!</span>
            </div>
            <button class="submit-button" type="submit">Submit</button>
        </form>
    </div>

</div>
