import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent implements OnInit {
  signupForm!: FormGroup;
  constructor() {}
  ngOnInit(): void {
    this.signupForm = new FormGroup({
      email: new FormControl('', [Validators.required, Validators.email]),
      password: new FormControl('', [
        Validators.required,
        Validators.minLength(5),
        Validators.pattern(
          /(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@$!%*#?&^_-]).{8,}/
        ),
      ]),
    });
  }
  onSubmit() {
    console.log(this.signupForm);
  }
}
<div class="login-container">
    <div class="login-content">
        <form [formGroup]="signupForm" (ngSubmit)="onSubmit()">
            <div class="form-group1">
                <label for="email" >Email</label>
                <input type="text" name="" id="email" formControlName="email" class="form-control">
                <span class="error-text" *ngIf="!signupForm.get('email').valid && signupForm.get('email').touched">Please enter a valid email!</span>
            </div>
            <div class="form-group2">
                <label for="password">Password</label>
                <input type="text" name="" id="password" formControlName="password"  class="form-control">
                <span class="error-text" *ngIf="!signupForm.get('password').valid && signupForm.get('password').touched">Please enter a valid password!</span>
            </div>
            <button mat-raised-button  style="background-color: #9BA3ED ; font-family: Inter Bold;  width: 170px ; height: 35px; text-align: center;margin-left: 230px; margin-top: 30px;" type="submit" [disabled]="signupForm.invalid">Sign In or Register</button>
            <!-- <button mat-raised-button  style="background-color: #FFFFFF ; font-family: Inter Bold; margin-right: 2%; width: 90px ; height: 35px; " (click)="handleLogin()">LOGIN</button> -->
        </form>
    </div>

</div>
